            // set active chapter
            this.$elChapters
                .removeClass('active')
                .filter(function() {
                    return $(this).data('chapter-id') == chapter.id;
                })
                .addClass('active');

            // find slider point
            var sliderPointIndex = null;
            this._slider.model.points.some(function(point, i) {
                if (point.data.chapter === chapter && point.data.section === section) {
                    sliderPointIndex = i;
                    return true;
                }
            });
